
public class Calculator {
	public int add(int a, int b) {
		return a + b ;
	}
	public int subtract(int a, int b) {
		return a - b ;
	}
	public int multiply(int n1, int n2) {
		return n1 * n2 ;
	}
	public int divide(int n1, int n2) {
		// TODO Auto-generated method stub
		return n1/n2;
	}
	
	 
}
